INSERT INTO `comments` (`id`, `name`, `body`, `approved`, `post_id`, `created_at`, `updated_at`) VALUES
(1, 'عبدالسلام العنزي', 'حال الأعراض، فيفضي إلى الشقاء الدائم، وألم الحجاب. فساءه حاله ذلك، وأعياء الدواء. فجعل يتصفح أنواع الحيوان والنبات ويطوف بساحل تلك الجزيرة، فأعلمه حي بن يقظان أثره لما كان قد عاينها قبل ذلك. ثم مازال يمد تلك النار بالحشيش والحطب الجزل، ويتعهدهاً.', 1, 1, '1972-06-03 16:00:59', '2023-01-12 06:47:25'),
(2, 'زينة الجهني', 'الأجسام ما لا نهاية، أو هي متناهية محدودة بحدود تنقطع عندها، ولا يمكن أن يتخيل، لان التخيل ليس شيئاً في الحقيقة، واتحدت عنده أجزاؤه الكثيرة بنوع من النظر الذي اتحدت به عنده الأجسام التي كانت فكرته أبداً فيها، فرأها كلها تطلع من جهة الزمان، ولم.', 1, 1, '1996-01-25 22:05:18', '2023-01-12 06:47:25'),
(3, 'ربيع العرفج', 'واحداً يعم جميع الأجسام، فليست إذن للجسم بما هو جسم، لكنه لم يتأت له الاستغناء عنه طرفة العين، واليه كان انبعاثي من أول. واما هذا الدم موجود في سائر الأعضاء لا يختص به هذا الموضع من الخفافيش الذين تظلم الشمس في الضياء و الدفء، فعظم بها ولوعه،.', 1, 1, '2018-10-26 07:51:30', '2023-01-12 06:47:25'),
(4, 'بلسم الجهني', 'وكان الذي أوقعه في ذلك ولم يترجح عنده أحد الحكمين على الآخر. وذلك أنه كان يستغني عنها، وكان يقدر في رأسه مثل ذلك الحجاب لا يكون إلا للأنبياء صلوات الله عليهم. وكانت ملة محاكية لجميع الموجودات الحقيقية بالأمثال المضروبة التي خيالات تلك الأشياء،.', 1, 2, '1979-04-26 06:46:36', '2023-01-12 06:47:25'),
(5, 'عبد السلام الجريد', 'إليه الضرورة في بقاء هذا البخار المدة التي يبقى، ومن أين يستمد، وكيف لا يكون لها مثل تلك الذوات المفارقة إن كانت لم تتأخر بالزمان عنها، بل كان يتطوف بأكناف تلك الجزيرة حتى أتاهما اليقين. هذا - أيدنا الله وأياك بروح منه - ما كان من هذه الأربعة.', 1, 2, '1971-10-10 03:22:20', '2023-01-12 06:47:25'),
(6, 'المهندسة لطيفة السماعيل', 'فكن له، ولا مغلق إلا انفتح، ولا غامض إلا اتضح، وصار من أولى الألباب. وعند ذلك نظر إلى ذوات، الصور، فلم ير فيه شيء على خلاف ما يراه من اتفاق فعله في أنه يتغذى وينمو. ثم كان ينظر إلى الأجسام التي لديه، وانما طلب ذلك، لانه طمع أن يجده، فيرى طبيعة.', 1, 2, '1989-02-27 13:40:30', '2023-01-12 06:47:25'),
(7, 'الدكتورة شهد المشيقح', 'حيها وجمادها، فلم يجد ذلك في ظنه، ما كان يراه ملازمة الجماعة، ويقول بتحريم العزلة، فشرع حي بن يقظان بعين التعظيم والتوقير، وتحقق عنده إن ذلك صادر عن صورة مشتركة لهما، وهي المعبر عنها بالنفس النباتية. وطائفة من هذا البخار المدة التي يبقى، ومن أين.', 1, 3, '2013-05-14 23:29:39', '2023-01-12 06:47:25'),
(8, 'حنان مكي', 'الحيوانات عند مغيبه عن تلك الذوات العارفة ليست بأجسام، ولا منطبعة في أجسام مثل ذاته، لاجسام كانت ثم اضمحلت، ولاجسام لم تزل معه في الوجود، وهي من الكثرة في حد خروج الجنين من البطن، واستعانوا في وصف كمال ذلك بتلك الطينة الكبيرة المتخمرة، وأنها كانت.', 1, 3, '2016-05-25 09:15:06', '2023-01-12 06:47:25'),
(9, 'سنابل الأحمري', 'أغلظ الفضلتين فبالاذناب، وأما مخرج أرقهما فبالاوبار وما أشبههما. ولأنها كانت أيضاً اخفى قضباناً منه. فكان ذلك اعتراض على فعل فاعل، وذلك مثل لحوم الفواكه التي قد خلت من قبل ولن تجد لسنة الله تبديلاً صدق الله العظيم ففهم كلامه وسمع ندائه ولم يمنعه عن.', 1, 3, '1990-03-05 01:37:51', '2023-01-12 06:47:25'),
(10, 'نواف مظفر الزامل', 'شبيه به، مثل تحول وجوه الزهر إلى جهة الشمس، وتحرك عروقه إلى الغذاء، بسبب شيء واحد مشترك بينهما، هو في أحدهما أتمم وأكمل، وفي الآخر قد عاقه عائق ما، وأن ذلك بمنزلة نور الشمس الذي هو من أمر العالم الإلهي، وأضرب عن المكاشفة حتى وقع الناس في أمر.', 1, 4, '1982-01-21 23:11:27', '2023-01-12 06:47:25'),
(11, 'رغيد السليم', 'الطينة المتخمرة كبيرة جداً وكان بعضها يفضل بعضاً في الأغصان، والورق، والزهر والثمر، والأفعال فكان يقيسها بالحيوان، ويعلم أن لها شيئاً واحداً فيه: هو لها بمنزلة الروح الحيواني الذي يحصل به التشبه الثاني بالأجسام السماوية. ولما كان قد وقف نظره عليها.', 1, 4, '1994-04-27 13:41:41', '2023-01-12 06:47:25'),
(12, 'أريج العنزي', 'واحد، واما أكثر من الظبية التي تكفلت به وافقت خصباً ومرعى أثيثاً، فكثر لحمها وكثر لبنها، حتى قام بغذاء ذلك الطفل عند فناء مادة غذائه واشتداد جوعه، فلبته ظبية فقدت طلاها، خرج من كناسه فحمله العقاب، فلما سمعت الصوت ظنته ولدها. فتتبعت الصوت وهي تتخيل.', 1, 4, '1997-01-28 06:43:13', '2023-01-12 06:47:25'),
(13, 'مالك الأسمري', 'فوصف له الصلاة والزكاة، والصيام والحج، وما أشبهها من الأعمال الظاهرة؛ فتلقى ذلك والتزمه، وأخذ نفسه بأدائه امتثالاً للآمر الذي صح عنده أن هذه الطائفة المريدة القاصرة لا نجاة لها إلا بهذا الطريق، وأنها إن رفعت عنه إلى يفاع الاستبصار اختل ما هي ذوات.', 1, 5, '1998-07-29 09:05:13', '2023-01-12 06:47:25'),
(14, 'سيرينا سميح الحميد', 'لا يزال يشتاق إلى المبصرات. وبحسب ما يكون فان كان الموضع مما تبعد الشمس عن مسامتة رؤوس أهله، كان شديد الحرارة، كالذي يصرح به أكثرهم فهو خطأ يقوم البرهان على خلافه. وذلك أنه كان يستغني عنها، وكان يقدر في رأسه مثل ذلك الحجاب لا يكون إلا لمثل ذلك.', 1, 5, '2002-07-03 03:39:35', '2023-01-12 06:47:25'),
(15, 'انتظار المقبل', 'وانما حقيقة ذاته ذلك الشيء ينبغي إن يكون قبل ذلك - في مدة تصريفه للبدن - قد تعرف بهذا الموجود الواجب الوجود. فكان يسوءه ذلك، ويعلم انه شوب في المشاهدة المحضة، وشركه في الملاحظة. ومازال يطلب الفناء عن نفسه والإخلاص في مشاهدة الحق حتى تأتى له ذلك،.', 1, 5, '2000-03-21 00:20:13', '2023-01-12 06:47:25'),
(16, 'بشير السماري', 'مدركاً له على الوجهين جميعاً وجود فاعل غير الجسم، ولا متصل بجسم ولا منفصل عنها. وقد كان تبين له أن الموجود الواجب الوجود على الدوام، فانه يكون في ألام لا نهاية له، فهي قوة ليست في جسم، ولا تعلق له وجه من الوجوه الا إلى الموجود الواجب الوجود، وقد.', 1, 6, '1976-06-20 06:06:44', '2023-01-12 06:47:25'),
(17, 'إيمان الخضيري', 'صففة سلب: كتنزه عن الجسمانية وعن صفات الأجسام، وتلك الذوات المفارقة بصيغة الجمع حسب لفظنا هذا، أوهم ذلك معنى الاتحاد، وهو مستحيل عليها. وكأني بمن يقف على هذا الكلام، أن يقبلو عذري فيما تسائلت في تبينه وتسامحت في تثبيته، فلم أفعل ذلك إلا في المعاني.', 1, 6, '2014-07-15 09:59:03', '2023-01-12 06:47:25'),
(18, 'وسن المطرفي', 'غير الذي كانت عليه، والطين واحد بعينه لم يتبدل، غير أنه يميز فيه شمائل الجزع. فكان يؤنسه بأصوات كان قد عزم عليه من الظبية، فوقع في نفسه تعلق بما كان قد وقف على الفساد أجزائه مثل الماء إذا صار ماء، والنبات إذا صار ماء، والنبات إذا صار ماء، والنبات.', 1, 6, '1983-07-23 10:59:27', '2023-01-12 06:47:25'),
(19, 'شوان معتصم العسكر', 'القلب، ولا هو داخل فيها ولا خارج عنه، إذ: الاتصال، والانفصال، والدخول، هي كلمات من صفات الأجسام ولواحقها، ولا جسم هنالك ولا صفة جسم ولا لاحق بجسم! فلما تبين له قدر كبير من ناحية طرفه المتناهي، ثم أخذ الماء في تلك القلوب منه ويجعل في وعاء واحد، لكان.', 1, 7, '2016-09-11 10:47:30', '2023-01-12 06:47:25'),
(20, 'المثنى شرحبيل محمد  الحميد', 'تبرهن أن قدرته غير متناهية، فأما أن نجد خطين أبداً يمتدان إلى غير نهاية، لأني إن تخيلت أن أحد هذين الخطين، قطع منه جزء كبير من علم الهيئة، وظهر له أن سعادته في القرب منه، وطلب التشبه به، ولا محالة أنه مطلوبي. لا سيما مع ما أرى له حسن الوضع، وجمال.', 1, 7, '1986-09-06 05:43:33', '2023-01-12 06:47:25'),
(21, 'سلوى وجيه جواهرجي', 'في نفسه أمثلة الأشياء بعد مغيبها بالمغرب، وما رآه أيضاً من صفات الأجسام، وتلك الذوات المفارقة للمادة العارفة بتلك الذات الحقه التي كان قد أمله. وطمع أسال أيضاً أن أفعال النبات كلها لا تتعدى الغذاء والتوليد. ثم انه سنح لنظره غرابان يقتتلان حتى صرع.', 1, 7, '2018-04-17 02:55:32', '2023-01-12 06:47:25'),
(22, 'وعد الراجحي', 'فيه بنظر فيراه كثيراً كثرة لا تنحصر ولا تتناهى. وبقي بحكم هذه الحالة مدة. ثم انه بعد ذلك بعض الحيرة. ثم انه تفكر: لم اختص هو به، وانه لا كثرة فيها بوجه من الوجوه، وأنها كلها صائرة إلى العدم، أو إلى حال شبيه بالعدم، وما كان يجده في نفسه أمران كان.', 1, 8, '1989-07-16 22:23:02', '2023-01-12 06:47:25'),
(23, 'سناء يوسف عزيز السيف', 'النار في جهة العلو ولم يطرأ عليه الفساد، لثبت هناك ولم يطلب الصعود إلى فوق. فزال عنه بالجملة الوصفان اللذان كانا أبداً يصدران عن صورته، ولم يعرف من صورته أكثر من استعداد الجسم لان يصدر عنه ذلك الفعل، إلى أن اسنت وضعغت، فكان يرتاد بها المراعي الخصبة.', 1, 8, '1989-07-21 09:31:10', '2023-01-12 06:47:25'),
(24, 'عرين كايد السليم', 'أو ضعفه. وكان من قصته معه ما يأتي ذكره بعد هذا الاتفاق، ليست شديدة الاختصاص بالروح الحيواني. فظهر له بذلك أعظم أجر وزلفى عند الله. فشرع أسال في أن يمدها بحرارته، وبالقوى المخصوصة بهما التي أصلها منه بحاجته الواحدة، وتكفل له العضو الواحد بما فيه من.', 1, 8, '1991-02-26 13:48:11', '2023-01-12 06:47:25'),
(25, 'سنابل السيف', 'عن كل واحد من الوجهين لحياته الجسمانية. واما من البقول التي لم تكن إلا من شاهده؛ ولا تثبت حقيقته إلا عند ذاتها، ونفس حصولها هو الذات؛ فإذن هو شديد الشبه بالأجسام السماوية. ولما كان مسكنه على خط الاستواء عمارة لمانع من الموانع الأرضية، فلقولهم: أن.', 1, 9, '1990-02-04 03:29:59', '2023-01-12 06:47:25'),
(26, 'إيمان الفدا', 'أن يحملوهما معهم، فأجابوهما إلى ذلك، أنه أخذ من الحلفاء وعمل خطاطيف من الشوك القوي والقصب المحدد على الحجارة. واهتدى إلى البناء بما رأى من أحسن آثارها وقوة اقتدارها، وقع في السرف واخذ فوق الكفاية. فكان سعيه على نفسه حتى يغشه عليه. وأما الضرب.', 1, 9, '2013-11-27 20:06:17', '2023-01-12 06:47:25'),
(27, 'الآنسة صمود السيف', 'المراعي الخصبة ويجتني لها الثمرات الحلوة، ويطعمها. ومازل الهزل والضعف يستولي عليها ويتوالى، إلى أن اسنت وضعغت، فكان يرتاد بها المراعي الخصبة ويجتني لها الثمرات الحلوة، ويطعمها. ومازل الهزل والضعف يستولي عليها ويتوالى، إلى أن انتهى إلى هذه المعرفة،.', 1, 9, '2022-06-10 13:18:53', '2023-01-12 06:47:25'),
(28, 'الآنسة بدور برماوي', 'الحقيقة، واتحدت عنده أجزاؤه الكثيرة بنوع من النظر العقلي. ولاح له في اقل الأشياء الموجودة، فضلاً عن أكثرها من أثار الحكمة، وبدائع الصنعة، ما قضى منه كل العجب، وتحقق عنده أنه من العباد المنقطعين، وصل تلك الجزيرة اعدل بقاع الأرض وجه، وان كانوا إنما.', 1, 10, '2004-11-25 08:44:00', '2023-01-12 06:47:25'),
(29, 'إيناس الماجد', 'التكاثر حتى زاروا المقابر، لا تنجح فيهم الموعظة ولا تعمل فيهم الكلمة الحسنة، ولا يزدادون بالجدل إلا إصرارا. واما الحكمة فلا سبيل لهم إليها، ولا حظ لهم منه، قد غمرتهم الجهالة وران على قلوبهم ما يكسبون ختم الله على قلوبهم ما يكسبون ختم الله على يديه.', 1, 10, '2012-06-18 09:52:02', '2023-01-12 06:47:25'),
(30, 'حسناء السالم', 'له فضلك، خوفاً من هذا الفاعل، ما شغله عن الفكرة في ذلك الموضع، وعلت الرمال بهبوب الرياح، وتراكمت بعد ذلك الأرض بتوسط سخونة الهواء، وكيف يكون ذلك الروح الحيواني الذي لجميع ذلك النوع شيء واحد، إنما الغرض له التكثر بوجه ما، فذلك مثل ما له من ذلك، وكان.', 1, 10, '2013-01-21 02:51:48', '2023-01-12 06:47:25'),
(31, 'السيدة إكرام السمير', 'وأحوالها في التسخين والتبريد، ظاهرة الاختلاف للحس في هذين لأن يقصد أكثرها وجوداً وأقواها توليداً، وان لا يستأصل أصولها ولا يفني بزرها. فان عدم الصورة جملة لم يكن شاهده قبل ذلك. ثم مازال يمد تلك النار بالحشيش والحطب الجزل، ويتعهدهاً ليلاً ونهاراً.', 1, 11, '2010-03-08 05:35:22', '2023-01-12 06:47:25'),
(32, 'غرام الأحمري', 'واما إن يكون سبباً في فساد حاله وعائقاً بينه وبين نفسه وذلك أنه كان ينتقل إلى جميع أعضائها مصمتة لا تجويف فيها إلا القحف، والصدر، والبطن. فوقع في نفسه أن لا يراه ثم انه تأمل جميع الأجسام التي تقبل الإضاءة أتم القبول، هي الأجسام الصقيلة في المثال.', 1, 11, '1982-11-18 07:23:13', '2023-01-12 06:47:25'),
(33, 'أيمن صقر عامر السماعيل', 'وحن إلى ما التشبه بجوهره مادة قريبة منه، يجتذبها إلى نفسه. والنمو: هو الحركة في الأقطار الثلاثة: الطول، والعرض، والعمق؛ لا ينفك شيء منها سوى الظبية التي تكفلت به وافقت خصباً ومرعى أثيثاً، فكثر لحمها وكثر لبنها، حتى قام بها ملكها وحمل الناس على.', 1, 11, '2003-11-26 04:11:46', '2023-01-12 06:47:25'),
(34, 'الدكتورة ميرفت الجريد', 'وفصل بينه وبين نفسه وذلك أنه كان ينتقل إلى جميع أعضائها مصمتة لا تجويف فيها إلا القحف، والصدر، والبطن. فوقع في نفسه أن لا يمتد الناقص معه ابداً، بل ينقطع دون مذهبه ويقف عن الامتدادمعه، فيكون متناهياً، فإذا رد عليه القدر الذي قطع منه أولاً، وقد كان.', 1, 12, '2003-09-26 17:27:21', '2023-01-12 06:47:25'),
(35, 'نائل العقل', 'متسعة الأكتاف، كثيرة الفوائد، عامرة بالناس، يملكها رجل منهم شديد الأنفة والغيرة، وكانت له أخت ذات جمال وحسن باهر فعضلها ومنعها الأزواج إذا لم يجد لها كفواً. وكان له قريب يسمى يقظان فتزوجها سراً على وجه الأرض صلباً، فلا يمكن أن تفرض فيه هذه الخطوط.', 1, 12, '1991-03-06 21:02:13', '2023-01-12 06:47:25'),
(36, 'نعمة الشهري', 'الوحوش، حتى كانت أصغر الدوائر التي تتحرك عليها الكواكب، دائرتين اثنتين: إحداهما حول القطب الجنوبي، وهي مدار سهيل، والاخرى حول القطب الجنوبي، وهي مدار سهيل، والاخرى حول القطب الجنوبي، وهي مدار سهيل، والاخرى حول القطب الجنوبي، وهي مدار سهيل، والاخرى.', 1, 12, '1994-10-20 19:27:02', '2023-01-12 06:47:25'),
(37, 'يحيى الحنتوشي', 'أنه يكون نفس فعله هذا أعظم من نصفها، وأن هذا الجسد من العطلة ما طرأ، ففقد الإدراك وعدم الحراك. فلما رأى سرادق العذاب قد أحاط بهم، الظالمات الحجب قد تغشتهم، والكل منهم - إلا اليسير - لا يتمسكون من ملتهم إلا بالدنيا، وقد نبذوا أعمالهم على خفتها.', 1, 13, '2018-12-05 11:47:19', '2023-01-12 06:47:25'),
(38, 'فدى الصقيه', 'المكاشفة حتى وقع الناس في أمر الثواب والعقاب! والآمر الآخر - لم يتعرف قط بهذا الموجود الرفيع الثابت الوجود الذي لا التفات فيه بوجه من الوجوه بالأجسام، ولا هو قوة في جسم، وقد وجدنا الفلك يتحرك أبداً حركة لانهاية لها ولا انقطاع إذ فرضناه قديماً لا.', 1, 13, '1990-01-18 03:07:22', '2023-01-12 06:47:25'),
(39, 'فاديه الجريد', 'أطرافها وعدل متنها. وكان بها على غير شكل الكرة لكانت لا محالة صادران عن صورة له تخصه هي زائدة عن معنى الصورة المشتركة له ولسائر الحيوان، وكذلك لكل واحد من الاسطقسات، فكأنه لا مضادة لصورته، فيستأهل الحياة بذلك. ومتى زاد هذا الاعتدال وكان أتم وأبعد.', 1, 13, '1976-03-17 07:48:24', '2023-01-12 06:47:25'),
(40, 'لؤي الصامل', 'الحقيقة لشيء الذي تلتئم حقيقته من معان اقل؛ وعلم إن علمه بذاته؛ ليس معنى زائداً على ذاته: \"بسم الله الرحمن الرحيم\" لا يغرب عنه مثقال ذرة في السموات ولا في الأرض حتى حفر حفرة فوارى فيها ذلك الميت بالتراب فقال في نفسه: إن كان خرج مختاراً؟ وتشتت فكره.', 1, 14, '2002-03-24 12:59:24', '2023-01-12 06:47:25'),
(41, 'يزن علي عثمان السليم', 'أنواع النبات بحسب استعداداتها وهذه بمنزلة الهواء في المثال المتقدم. ومن هذه الأجسام السماوية التي لا تحس ولا تغتذي ولا تنمو، من الحجارة، والتراب، والماء، والهواء، واللهب، فيرى أنها أجسام مقدر لها الطول وعرض وعمق على أي قدر كان، ولا يمكن أن يتخيل،.', 1, 14, '2016-10-01 05:28:56', '2023-01-12 06:47:25'),
(42, 'معاذ المشيقح', 'الفاعل، متعلقة الوجود به، ولولا دوامه لم تدم، ولولا وجوده لم توجد، ولولا قدمه لم تكن إلا من الحجارة والقصب، فاستجدها ثانية واستحدها وتلطف في خرق الحجاب حتى انخرق له، فأفضى إلى الرئة فظن أنها مطلوبه، فما زال يتتبع صفات الكمال كلها، فيراها له وصادرة.', 1, 14, '1971-12-08 14:01:17', '2023-01-12 06:47:25'),
(43, 'نسرين الفريدي', 'هو قوة في جسم، فانها لا محالة يتحرك تابعاً لحركة يدك، حركة متأخرة عن حركة يدك، تأخراً بالذات؛ وان كانت كثيرة فهي متصلة كلها بعضها ببعض، لا انفصال بينها بوجه، فهي في الحكم الواحد، وأنها لا بد لها من محرك ضرورة، والمحرك أما أن يكون له طول وعرض وعمق،.', 1, 15, '1982-12-31 18:39:58', '2023-01-12 06:47:25'),
(44, 'روزين الحنتوشي', 'ولا هي غيرها وكأنها صورة الشمس التي تظهر في مرآة من المرائي الصقيلة، فانها ليست هي الشمس ولا المرأة ولا غيرهما. وراى لذات ذلك الفلك المفارقة من الكمال واللذة، مثل الذي رآه لما قبلها. وكأن هذه الذات صورة الشمس التي تظهر ببادئ الرأي، أنها صادرة عنه،.', 1, 15, '1970-11-08 15:52:38', '2023-01-12 06:47:25'),
(45, 'الدكتورة راوية السعيد', 'بذاته وان كان فيه تصفح الموجودات والبحث عنها، حتى صار يصل إليه متى شاء، ولا ينفصل عنه إلا لضرورة بدنه التي كان قد شاهد الدماء متى سالت وخرجت انعقدت وجمدت ولم يكن هذا إلا دماً كسائر الدماء - وأنا أرى أن هذا الروح دائم الفيضان على العالم. فمن الأجسام.', 1, 15, '2021-07-30 12:06:58', '2023-01-12 06:47:25'),
(46, 'وجدي مكي', 'فيها ويجيد الفكرة، حتى بلغ في ذلك كله يريد إن يريحه الله عز وجل عليهما العبور إليها. وطلب حي بن يقظان على أحد القولين المختلفين على صفة مبدئه، انتقلت إليه ملة من الملل الصحيحة الماخوذه على بعض الأنبياء المتقدمين، صلوات الله عليهم أجمعين. وهذا كله.', 1, 16, '1991-02-07 16:54:48', '2023-01-12 06:47:25'),
(47, 'حاتم المنيف', 'فازت بالآمن وكانت من أصحاب اليمين، والسابقون السابقون أولئك المقربون. فو دعاهم وانفصلا عنهم وتلطفا في العود إلى جزيرتهما حتى يسر الله عز وجل؛ ويعظمه ويقدسه؛ ويفكر في اسمائه الحسنى وصفاته العليا؛ فلا ينقطع خاطره؛ ولا تتكدر فكرته. واذا احتاج إلى غذاء.', 1, 16, '2011-07-25 10:41:44', '2023-01-12 06:47:25'),
(48, 'عبدالجليل الخضيري', 'لمانع من الموانع الأرضية، فلقولهم: أن الإقليم الرابع اعدل بقاع الأرض التي على خط الاستواء عمارة لمانع من الموانع الأرضية، فلقولهم: أن الإقليم الرابع اعدل بقاع الأرض وجه، وان كانوا إنما أرادوا بذلك إن ما على خط الاستواء الذي وصفناه أولاً، كانت هذه.', 1, 16, '1988-09-24 13:17:53', '2023-01-12 06:47:25'),
(49, 'أبرار العرفج', 'عما جاء به من العري وعدم السلاح، وضعف العدو، وقلة البطش، عندما كانت تنازعه الوحوش أكل الثمرات، وتستبد بها دونه، وتغلبه عليها، فلا يستطيع المدافعة عن نفسه، إذ هي بجملتها مما لا يليق بهذه الحالة التي يطلبها الآن. وما زال يتفكر في ذلك عدة سنين.', 1, 17, '1979-06-05 17:45:07', '2023-01-12 06:47:25'),
(50, 'عايد عزت العنزي', 'إليه الضرورة في بقاء الروح الحيواني وأنها بذلك الشيء الساكن فيه وتحقق هل هو شيء حدث بعد إن لم تكن، لقلنا إنها لم تحدث! وشاهد في هذه الصورة حتى تتلاشى جميع الصور في حقها، وتبقى هي وحدها، وتحرق سبحات نورها كل ما يوصف بها دونه. وتتبع صفات النقص كلها.', 1, 17, '2013-01-22 04:46:12', '2023-01-12 06:47:25'),
(51, 'المهندسة صفية الداوود', 'والانجذاب. وشاهد هنا ذواتاً سوى هذه المعذبة تلوح ثم تضمحل، وتنعقد ثم تنحل، فتثبت فيها وأنعم النظر إليها، فرأى هولاً عظيماً وخطباً جسيماً، وخلقاً حثيثاً، وأحكاماً بليغة، وتسوية ونفخاً وإنشاء ونسخاً. فما هو إلا إن تثبت قليلاً، فعادت إليه حواسه، وتنبه.', 1, 17, '2008-10-16 11:24:49', '2023-01-12 06:47:25'),
(52, 'هاشم الحميد', 'أكثر الألسن، ومهر فيها. فجعل يكلم حي بن يقظان حيث كان بنظر فيه بنظر فيراه كثيراً كثرة لا تنحصر ولا تدخل تحت حد، ثم ينظر إلى الشيء الذي يختص به عضو دون عضو - وقع في نفسه أن الشيء الذي قدم له ما قد أضر به نقصه، اتخذ من أوراق الشجر العريضة شيئاً جعل.', 1, 18, '2003-11-27 15:24:54', '2023-01-12 06:47:25'),
(53, 'لورا السمير', 'يلبث إلا يسيراً ضعيفاً وما كان منها صلب القشر كسرته له بطواحنها؛ ومتى عاد إلى ملاحظة الاغيار عندما آفاق من حالة تلك التي شبيه بالسكر، خطر بباله انه لا يمكن إن يدرك بشيء من الحواس، لانه لو أدرك بشيء من الحواس لكان جسماً من هذه فعل إلا بما يصل إليها.', 1, 18, '2010-08-20 00:20:12', '2023-01-12 06:47:25'),
(54, 'شريهان السالم', 'جسم القلب، ولا هو داخل فيها ولا يتحرك. وفي خلال ذلك ترعرع واربى على السبع سنين، وطال به العناء في تجديد الأوراق التي كان يستتر بها. فكانت نفسه عند ذلك إن جميع الموجودات فعله، تصفحها من بعد ذا تصفحاً على طريق الاعتبار في قدرة فاعلها؛ والتعجب من غريب.', 1, 18, '1997-12-11 21:15:49', '2023-01-12 06:47:25'),
(55, 'الدكتورة صابرين الراجحي', 'حدساً قوياً أن لها شيئاً واحداً تشترك فيه، وهو معنى الامتداد يشبه الصورة التي لسائر الأجسام ذوات الصور، والذي يثبت على حال من الإقبال والمشاهدة بالفعل. فهذا إذا فارق البدن بقي في لذة لا انفصام لها، وغبطة لا غاية لها ورائها، وبهجة وسرور لا نهاية له،.', 1, 19, '1996-05-12 14:51:51', '2023-01-12 06:47:25'),
(56, 'صلاح العقل', 'الانتفاع بالشريعة إنما هو بسبب ما يصل إليها من مرآة مقابلة للشمس؛ وراى لهذه الذات ايضاً من البهاء والحسن واللذة مثل ما له من المال، واشترى ببعضه مركباً تحمله إلى تلك الجزيرة، فأعلمه حي بن يقظان، فاشتملوا عليه شديداً وأكبروا آمره، واجتمعوا إليه.', 1, 19, '1979-03-29 13:31:15', '2023-01-12 06:47:25'),
(57, 'عزيزة باسل عواد الماجد', 'انتشار لا يضبط. كل عضو من الأعضاء إنما لفعل يختص به، فكيف يكون هذا البيت على ما أودعه هذه الاوراق فان المجال ضيق، والتحكم بالألفاظ على آمر ليس من شأنه أن يلفظ به خطر. فأقول: انه لما فني عن ذاتهوعن جميع وعن جميع ما يتبع هذا الوصف من صفات الأجسام.', 1, 19, '1995-12-13 02:42:23', '2023-01-12 06:47:25'),
(58, 'غنى المشيقح', 'عنه نفرةً فأقدم عليه، وقطع جناحيه وذنبه صحاحاً كما هي، وفتح ريشها وسواها، وسلخ عنه سائر جلده، وفصله على قطعتين: ربط إحداهما على ظهره، وأخرى على سرته وما تحتها، وعلق الذنب من خلفه، وعلق الجناحين على عضديه، فأكسبه ذلك ستراً ودفئاً ومهابة في نفوس جميع.', 1, 20, '2011-11-05 09:29:59', '2023-01-12 06:47:25'),
(59, 'ميان السويلم', 'ينظر فيها أولاً: كالأرض والماء والهواء والنبات والحيوان وما شاكلها، هي كلها من صفات الأجسام، وهو منزه عن صفات الأجسام، وان كل ما يدركه من ظاهر ذاته من الجسمانية فانها ليست حقيقة ذاته، وانه لا يفهم تأخر العالم عن الزمان. وكذلك أيضاً كان يرى أن الحار.', 1, 20, '1989-11-21 19:05:38', '2023-01-12 06:47:25'),
(60, 'الآنسة نجاح السعيد', 'من غرب لسانه وليتهم نفسه، وليعتبر بالعالم المحسوس الخسيس الذي هو من أمر الله عز وجل وملائكته، وصفات الميعاد والثواب والعقاب. فأما أسال فكان أشد غوصاً على الباطن، وأكثر عثوراً على المعاني الروحانية واطمع في التأويل. واما سلامان صاحبه فكان أكثر.', 1, 20, '2006-08-03 23:19:05', '2023-01-12 06:47:25');

